interface INFTFlags {
    function mint(address _recipient, uint256 _challengeId) external;

    function hasMinted(address _recipient, uint256 _challengeId) external view returns (bool);

    function tokenIdCounter() external view returns (uint256);
}

contracts/S1/Season1Challenge2.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

import "../INFTFlags.sol";

contract Season1Challenge2 {
    address public nftContract;

    constructor(address _nftContract) {
        nftContract = _nftContract;
    }

    function justCallMe() public {
        require(msg.sender != tx.origin, "Not allowed");
        INFTFlags(nftContract).mint(tx.origin, 2);
    }
}

contracts/S1/Season1Challenge2Solution.s.content:
//SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

import "./Season1Challenge2.sol";

contract Season1Challenge2Solution {
    address public season1Challenge2;

    constructor(address _season1Challenge2) {
        season1Challenge2 = _season1Challenge2;
    }

    function mintFlag() public {
        Season1Challenge2(season1Challenge2).justCallMe();
    }
}
